import useForm from '../lib/useForm';

export default function CreateProduct() {
  const [inputs, handleChange] = useForm({
    name: 'Nice Shoes',
    price: 34234,
    description: 'These are nice shoes',
  });
  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Name"
          value={inputs.name}
          onChange={(e) => {
            console.log(e.target.value);
            handleChange(e.target.value);
          }}
        />
      </label>
    </form>
  );
}
