import useForm from '../lib/useForm';
import Form from './styles/Form';

export default function CreateProduct() {
  const { inputs, handleChange, resetForm, clearForm } = useForm({
    name: 'Nice Shoes',
    price: 34234,
    description: 'These are nice shoes',
  });

  return (
    <Form>
      <fieldset>
        <label htmlFor="image">
          Image
          <input
            type="text"
            id="image"
            name="image"
            placeholder="image"
            value={inputs.image}
            onChange={(e) => {
              console.log(e.target.value);
              handleChange(e.target.value);
            }}
          />
        </label>
        <label htmlFor="name">
          Name
          <input
            type="text"
            id="name"
            name="name"
            placeholder="Name"
            value={inputs.name}
            onChange={(e) => {
              console.log(e.target.value);
              handleChange(e.target.value);
            }}
          />
        </label>
        <label htmlFor="price">
          Price
          <input
            type="number"
            id="price"
            name="price"
            placeholder="price"
            value={inputs.price}
            onChange={(e) => {
              handleChange(e.target.value);
            }}
          />
        </label>

        {
          // <button type="button" onClick={clearForm}>
          //   Clear Form
          // </button>
          // <button type="button" onClick={resetForm}>
          //   Reset Form
          // </button>
        }
        <button type="submit">+ Add Product</button>
      </fieldset>
    </Form>
  );
}
